"use strict";(self.webpackChunksite=self.webpackChunksite||[]).push([[5666],{5666:(e,n,t)=>{t.r(n),t.d(n,{default:()=>s});const s='import React, { useState } from \'react\';\nimport { Progress, Button } from \'xun-components\';\n\nconst Demo = () => {\n  const [percent, setPercent] = useState(10);\n\n  const handleAdd = () => {\n    if (percent >= 100) {\n      return;\n    }\n    setPercent(percent + 10);\n  };\n\n  const handleMinus = () => {\n    if (percent <= 0) {\n      return;\n    }\n    setPercent(percent - 10);\n  };\n\n  return (\n    <div className="demo-page">\n      <header>\n        <h3 className="demo-page-title">圆形进度条</h3>\n      </header>\n      <main>\n        <div className="demo-block">\n          <div className="demo-title">基本</div>\n          <Progress percent={percent}></Progress>\n        </div>\n        <div className="demo-block">\n          <div className="demo-title">设置线条宽度</div>\n          <Progress percent={percent} strokeWidth="2"></Progress>\n        </div>\n        <div className="demo-block">\n          <div className="demo-title">设置线条颜色</div>\n          <Progress\n            percent={percent}\n            strokeWidth="2"\n            strokeColor="red"\n          ></Progress>\n        </div>\n\n        <Button type="primary" onClick={handleAdd}>\n          增加\n        </Button>\n        <Button onClick={handleMinus}>减少</Button>\n      </main>\n    </div>\n  );\n};\n\nexport default Demo;\n'}}]);