"use strict";(self.webpackChunksite=self.webpackChunksite||[]).push([[8314],{8314:(e,n,t)=>{t.r(n),t.d(n,{default:()=>s});const s='import React, { useState } from \'react\';\nimport { ProgressBar, Button } from \'xun-components\';\n\nconst Demo = () => {\n  const [percent, setPercent] = useState(10);\n\n  const handleAdd = () => {\n    if (percent >= 100) {\n      return;\n    }\n    setPercent(percent + 10);\n  };\n\n  const handleMinus = () => {\n    if (percent <= 0) {\n      return;\n    }\n    setPercent(percent - 10);\n  };\n\n  return (\n    <div className="demo-page">\n      <header>\n        <h3 className="demo-page-title">进度条</h3>\n      </header>\n      <main>\n        <div className="demo-block">\n          <div className="demo-title">基本</div>\n          <div className="demo-body white">\n            <ProgressBar percent={percent}></ProgressBar>\n          </div>\n        </div>\n        <div className="demo-block">\n          <div className="demo-title">设置线条宽度</div>\n          <div className="demo-body white">\n            <ProgressBar percent={percent} strokeWidth="6"></ProgressBar>\n          </div>\n        </div>\n        <div className="demo-block">\n          <div className="demo-title">设置线条颜色</div>\n          <div className="demo-body white">\n            <ProgressBar\n              percent={percent}\n              strokeWidth="8"\n              strokeColor="red"\n            ></ProgressBar>\n          </div>\n        </div>\n\n        <Button type="primary" onClick={handleAdd}>\n          增加\n        </Button>\n        <Button onClick={handleMinus}>减少</Button>\n      </main>\n    </div>\n  );\n};\n\nexport default Demo;\n'}}]);